import '../styles/VoteView.css';
import React from 'react';
import Gallery from 'react-photo-gallery';
import { db, fbStorage } from '../utils/firebase.js';
import { Typography, Image, Button, Skeleton, BackTop, Popconfirm, Result} from 'antd';
import { withRouter } from 'react-router-dom';
import { UserOutlined } from '@ant-design/icons';

const clientId = process.env.REACT_APP_CLIENT_ID;
const redirectUri = process.env.REACT_APP_REDIRECT_URI;
const authUri = `https://discord.com/api/oauth2/authorize?client_id=${clientId}&redirect_uri=${encodeURI(redirectUri)}&response_type=token&scope=identify`;

const { Title } = Typography;

export class VoteView extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            imageLeft: '',
            imageRight: '',
            galleryUrls: [],
            isButtonDisabled: false,
            isLoading: true
        };
    }

    async componentDidMount() {
        this.props.onRouteChange('3');
        // TODO: Get if user voted or not
        await Promise.all([
            this.getImages(),
            this.userVoteCheck()
        ]);
        this.setState({ isLoading: false });
    }

    componentDidUpdate(prevProps) {
        if (this.props.userData !== prevProps.userData) {
            this.userVoteCheck();
        }
    }

    userVoteCheck = async () => {
        if (this.props.userData.userName) {
            try {
                const collection = db.collection('userData').doc(`${this.props.userData.userName}`);
                const doc = await collection.get();
                if (doc) {
                    const data = doc.data();
                    if (data.hasVoted)
                    this.setState({ isButtonDisabled: true });
                }
            } catch (error) {
                console.log(error);
            }
        }
        
    }

    toggleVote = async () => {
        try {
            const collection = db.collection('userData').doc(`${this.props.userData.userName}`);
            const doc = await collection.get();
            if (doc) {
                const data = doc.data();
                if (!data.hasVoted) {
                    collection.set({
                        ...data,
                        hasVoted: true
                    });
                }
                /* The below block should never be used */
                // else {
                //     collection.set({
                //         ...data,
                //         hasVoted: false
                //     });
                // }
            }
        } catch (error) {
            console.log(error);
        }
    }

    onLeftVoteButtonClick = async () => {
        try {
            const collection = db.collection('config').doc('voteCount');
            const doc = await collection.get();
            const data = doc.data();
            const voteCount = doc.data().left;

            collection.set({ ...data, left: voteCount + 1 });

            this.toggleVote();
            this.setState({ isButtonDisabled: true });
        } catch (err) {
            console.log(err);
        }
    }

    onRightVoteButtonClick = async () => {
        try {
            const collection = db.collection('config').doc('voteCount');
            const doc = await collection.get();
            const data = doc.data();
            const voteCount = doc.data().right;

            collection.set({ ...data, right: voteCount + 1 });

            this.toggleVote();
            this.setState({ isButtonDisabled: true });
        } catch (err) {
            console.log(err);
        }
    }

    getImages = async () => {
        try {
            /* Get voting images */
            const collection = db.collection('config').doc('uploadCompetition');
            const doc = await collection.get();
            /* Set State for Images */
            this.setState({
                imageLeft: doc.data().voteImage1,
                imageRight: doc.data().voteImage2,
            });
            /* Get carousel images */
            const votingDirRef = fbStorage.child('voting/carousel');
            const votingPictures = await votingDirRef.listAll();

            /* Note: This is nastyyyyyyy */
            const photoUrls =  await Promise.all(votingPictures.items.map(async (val) => {
                const photoPath = val.location.path_;
                const photoUrl = await fbStorage.child(photoPath).getDownloadURL();
                return { src: photoUrl, width: 1, height: 1};
            }));

            /* Set State for Carousel */
            this.setState({ galleryUrls: photoUrls });
        } catch (err) {
            console.log(err);
        }
    }

    Content = () => {
        if (this.state.isLoading) {
            return <Skeleton active paragraph={{ rows: 5 }}/>
        }
        else {
            if (this.props.userData.userName)
            {
                const popConfirmProps = {
                    title: "Are you sure you want to vote for this?",
                    okText: "Yes",
                    cancelText: "No",
                    disabled: this.state.isButtonDisabled
                };
    
                const buttonProps = {
                    className: 'vote-view-btn',
                    type: 'primary',
                    disabled: this.state.isButtonDisabled
                };
                
                let voteText = '';
                if (this.state.isButtonDisabled)
                    voteText = 'You have already voted';
                else
                    voteText = 'Vote';
    
                return (
                    <div className='vote-view-container'>
                        <BackTop />
                        <Title className='vote-view-title' level={3}>Please make a vote selection below</Title>
    
                        <Image 
                            className='vote-view-image-left'
                            src={this.state.imageLeft}
                            fallback='data:image/png;base64,'
                        />
                        <Image 
                            className='vote-view-image-right'
                            src={this.state.imageRight}
                            fallback='data:image/png;base64,'
                        />
    
                        <Popconfirm {...popConfirmProps} onConfirm={this.onLeftVoteButtonClick}>
                            <Button {...buttonProps}>{voteText}</Button>
                        </Popconfirm>
    
                        <Popconfirm {...popConfirmProps} onConfirm={this.onRightVoteButtonClick}>
                            <Button {...buttonProps}>{voteText}</Button>
                        </Popconfirm>
    
                        <Title className='vote-view-title-carousel' level={3}>Submissions Gallery</Title>
    
                        <div className='vote-view-gallery'>
                            <Gallery photos={this.state.galleryUrls} />
                        </div>
                    </div>
                );
            }
            else {
                return (
                    <Result
                        status='500'
                        title='Please log in to upload'
                        extra={<Button type='primary' icon={<UserOutlined />} href={authUri}>Login</Button>}
                    />
                );
            }
        }
    }

    render() {
        return (
            <>
                <this.Content />
            </>
        );
    }
}

export default withRouter(VoteView);